plugins {
    kotlin("multiplatform") apply false
    kotlin("plugin.serialization") apply false
}

allprojects {
    group = "moe.nikky"
    version = "1.0-SNAPSHOT"

    repositories {
        mavenCentral()
    }
}

tasks {
    val subprojects = listOf(
        project(":traktor-export-converter"),
        project(":rekordbox-export"),
        project(":mixxx-export"),
    )
    val packageZip by registering(Zip::class) {
        group = "package"
        subprojects.forEach { subproject ->
//            dependsOn(it.tasks["linkDebugExecutableMingwX64"])
            from(subproject.tasks["linkDebugExecutableMingwX64"])
        }
        archiveBaseName.set("dist")
        destinationDirectory.set(project.layout.buildDirectory)
    }
    val copyExecutables by registering(Copy::class) {
        group = "package"
        subprojects.forEach { subproject ->
//            dependsOn(it.tasks["linkDebugExecutableMingwX64"])
            from(subproject.tasks["linkDebugExecutableMingwX64"])
        }
        destinationDir = project.layout.buildDirectory.get().asFile
    }
}